package stringalgorithms;

/*
Problem Description : Given a string A of length N consisting of lowercase alphabets. Find the period of the string.
Period of the string is the minimum value of k (k >= 1), that satisfies A[i] = A[i % k] for all valid i.
Problem Constraints
1 <= N <= 106
 */
public class PeriodOfString {
    public int solve(String A) {
        int N = A.length();
        int[] z_arr = new int[N];

        if (N == 1) {
            return 1;
        }

        compute_Z_array(z_arr, A, N);
        int k = 1;
        while (k < N) {
            if (z_arr[k] == N - k) {
                return k;
            }
            k++;
        }
        return N;
    }

    // Z-String Algorithm
    void compute_Z_array(int[] Z, String A, int n) {
        int left = 0, right = 0; // left & right of the YELLOW box
        for (int i = 1; i < n; i++) {
            // Brute force part to find
            // the YELLOW box after the WHILE loop ends
            if (i > right) {
                left = right = i;
                while (right < n && A.charAt(right) == A.charAt(right - i)) {
                    right++;
                }
                Z[i] = right - left;
                right--;
            } else {
                if (Z[i - left] < right - left + 1) {
                    Z[i] = Z[i - left];
                } else {
                    left = i;
                    while (right < n && A.charAt(right) == A.charAt(right - i)) {
                        right++;
                    }
                    Z[i] = right - left;
                    right--;
                }
            }
        }
    }

    public static void main(String arg[]) {
        PeriodOfString periodOfString = new PeriodOfString();
        System.out.println(periodOfString.solve("abababab"));
        System.out.println(periodOfString.solve("aaaa"));
        System.out.println(periodOfString.solve("conanconanconancon"));
        System.out.println(periodOfString.solve(""));
    }
}
